import json

import requests
from bs4 import BeautifulSoup, NavigableString, Tag


def patch_finder(patch_notes_new):
    url_id = patch_notes_new[0].split()[1].replace('.', '-')
    url = "https://www.leagueoflegends.com/en-gb/news/game-updates/patch-" + url_id + "-notes/"
    return url


def check_if_new_patch():
    # Read saved file for comparison
    patch_notes_old = []
    with open("./already_crawled.txt", "r") as file:
        for line in file:
            text = line.replace('\n', '')
            patch_notes_old.append(text)

    # Crawls and writes recent patchnotes.
    url = "https://www.leagueoflegends.com/en-gb/news/tags/patch-notes/"
    response = requests.get(url)
    soup = BeautifulSoup(response.content, "lxml")

    patch_notes_new = []
    f = open("already_crawled.txt", "w")
    for quote in soup.find_all('h2'):
        f.writelines(quote.text + "\n")
        patch_notes_new.append(quote.text)
    f.close()

    # Checks if the recent patch_notes are newer than the saved ones.
    if patch_notes_old[0] == patch_notes_new[1]:
        print("We have a new Patch!")
        return patch_finder(patch_notes_new)
    else:
        print("Sadly no new patch")
        # input("Press enter and exit")
        # TODO: REMOVE LATER FOR TESTING ONLY
        return patch_finder(patch_notes_new)


url = check_if_new_patch()

response = requests.get(url)
soup = BeautifulSoup(response.content, "lxml")

# Find the start header
start_header = soup.find('h2', string="Big Gameplay Changes")

# Collect content between headers
content = []
collect = 0
if start_header:
    for element in start_header.next_elements:
        if isinstance(element, Tag):  # Check if the element is a tag
            if element.name == "h2":  # Found another h2 tag
                if collect == 2:  # If we've been collecting, stop as we hit another h2
                    break
                collect += 1  # Start collecting after hitting the first h2
                content.append("THIS_IS_FOR_SPLITTING")
            if collect:
                content.append(element.prettify())

# Split at the marker
html_items = []
html_champions = []
current_section = []
section_counter = 0

for item in content:
    if item == "THIS_IS_FOR_SPLITTING":
        if current_section:
            if section_counter == 0:
                html_items = current_section
            else:
                html_champions.append(current_section)
            current_section = []
            section_counter += 1
    else:
        current_section.append(item)

# Check for any remaining content after the last marker
if current_section:
    if section_counter == 0:
        html_items = current_section
    else:
        html_champions.append(current_section)

# Save the content to a JSON file
with open('extracted_content.json', 'w', encoding='utf-8') as file:
    json.dump({
        'html_items': html_items,
        'html_champions': html_champions
    }, file, indent=4)



