# Parse the HTML with BeautifulSoup
soup = BeautifulSoup(html_content, 'html.parser')

# Define a simple HTML structure for displaying the patch notes
html_output = '''
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Patch Notes Analysis</title>
    <style>
        body { font-family: Arial, sans-serif; margin: 40px; }
        .patch-note { background-color: #f4f4f4; padding: 20px; border-radius: 8px; margin-bottom: 20px; }
        .patch-note h3 { color: #333; }
        .summary, blockquote { color: #666; }
        img { width: 100px; }
    </style>
</head>
<body>
    <h1>League of Legends Patch Notes Analysis</h1>
'''

# Iterate through each champion change block
for block in soup.select('.patch-change-block'):
    champion_name = block.find('h3').text.strip()
    champion_link = block.find('a')['href']
    champion_image = block.find('img')['src']
    summary = block.find('p', class_='summary').text.strip()
    context = block.find('blockquote', class_='blockquote').text.strip()

    # Append the champion block to the HTML output
    html_output += f'''
    <div class="patch-note">
        <h2>Changes for {champion_name}</h2>
        <a href="{champion_link}">
            <img src="{champion_image}" alt="{champion_name}">
        </a>
        <h3>Summary of Changes</h3>
        <p>{summary}</p>
        <h3>Developer Commentary</h3>
        <blockquote>{context}</blockquote>
    </div>
    '''

# Close the HTML tags
html_output += '''
</body>
</html>
'''

# Output the HTML to a file
with open('patch_notes.html', 'w', encoding='utf-8') as file:
    file.write(html_output)

print("HTML file has been created!")